package main

import (
	"fmt"
	"sort"
)

// 超时
func findAnagrams1(s string, p string) []int {

	pp := []byte(p)
	sort.SliceStable(pp, func(i, j int) bool {
		return string(pp[i]) < string(pp[j])
	})

	set := make(map[string]struct{}, len(p))
	for _, v := range p {
		set[string(v)] = struct{}{}
	}

	if len(p) > len(s) {
		return []int{}
	}

	res := make([]int, 0, 8)
	i := len(p) - 1
	for i < len(s) {
		if _, ok := set[string(s[i])]; !ok {
			i++
			continue
		}
		temp := []byte(s[i-(len(p)-1) : i+1])
		sort.SliceStable(temp, func(i, j int) bool {
			return string(temp[i]) < string(temp[j])
		})
		if string(temp) == string(pp) {
			res = append(res, i-(len(p)-1))
		}
		i++

	}
	return res
}

func findAnagrams(s string, p string) []int {
	res := make([]int, 0, 8)
	pL := len(p)
	if len(p) > len(s) {
		return res
	}
	target := [26]int{}
	for _, v := range p {
		target[v-97]++
	}

	temp := [26]int{}
	for i := 0; i < pL; i++ {
		temp[s[i]-97]++
	}

	if temp == target {
		res = append(res, 0)
	}
	for i := pL; i < len(s); i++ {
		temp[s[i]-97]++
		temp[s[i-pL]-97]--
		if temp == target {
			res = append(res, i-pL+1)
		}
	}
	return res
}

func main() {
	s := "cbaebabacd"
	p := "abc"

	// s := ""
	// p := "yqrbgjdwtcaxzsnifvhmou"
	res := findAnagrams(s, p)
	fmt.Println(res)
}
